import { useState } from "react";
import Cabecera from "./components/Cabecera";
import Listado from "./components/Listado";
import GlobalStyle from "./Styles";
import { Contenedor } from './Styles';

// El componente App es el padre de:
// - Cabecera
// - Listado
// ESTADO: App debe manejar en su estado un número para contabilizar el total de elementos comprados.
// MÉTODOS: App debe tener un método para aumentar este número y que pueda ser ejecutado por su nieto Item.
// PROPS: App deberá pasar por props lo necesario a sus componenetes internos.

function App() {
  const [count, setCount] = useState(0);
  const [view, setView] = useState(true);

  const changeView = () => {
    setView(!view);
  };

  return (
    <Contenedor className="App">
      <Styles />
      <div className={view ? "capa" : "capa-oculta"}>
      </div>
      <div className={view ? "shop" : "shop-oculta"}>
        <Cabecera count={count} />
        <Listado setCount={setCount} />
      </div>
      <div className="button">
        <button onClick={() => changeView()}>{view ? 'Ver el video' : 'Ir a Comprar'}</button>
      </div>
    </Contenedor>
  );
}

export default App;
