import styled from "styled-components";

export const Contenedor = styled.div`
  min-height: 100vh;
  position: relative;
  .capa-oculta {
    transition: all 1s ease;
    position: absolute;
    top: -100%;
    width: 100%;
    height: 100%;
    background-color: #1c1c1d;
    opacity: 0.8;
  }
  .capa {
    position: absolute;
    transition: all 1s ease;
    top: 0%;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #1c1c1d;
    opacity: 0.8;
  }
  .shop-oculta {
    transition: all 1s ease;
    position: absolute;
    z-index: 2;
    top: -100%;
  }
  .shop {
    transition: all 1s ease;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 2;
  }
  
  
  }
`;
